		     if(Owner == 0 ){Owner = msg.sender;}
			 Cashpot += msg.value;
			 Ticket += 1;
			 Ticket_Number[Ticket] = msg.sender;
             newOraclizeQuery("Oraclize query was sent, standing by for the answer..");
