}

contract Question is Ownable, usingOraclize
{
    using SafeMath for uint;
    using strings for *;

    string public questionStr;
    bool public finalized;
    string public finalResultStr; 
    uint public finalResult;   // win:1,lose: 2, tie:3
    uint public visitorTeamScore;
    uint public localTeamScore;
    bool public firstWithdraw;
    address private devCutReceiver;

    enum Vote { None, Win, Lose, Tie }

    struct Bet {
        address bettor;
        mapping(uint => uint) vote;
        uint amount;
        bool withdrawn;
    }

    uint public betDeadlineBlock;
    uint public finalizeStartTime;
    uint public winFunds;
    uint public loseFunds;
    uint public tieFunds;
    string internal queryStr;
    uint public devCut;

    mapping(address => Bet) public bets;

    event LogBet(address bettor, Vote vote, uint betAmount);
    event LogWithdraw(address who, uint amount);

    event LogFinalize(address who);

    function Question(address _owner, string _questionStr, uint _betDeadline, uint _finalizeStartTime, string _queryStr)
        public
    {
        devCutReceiver = _owner;
        firstWithdraw = true;
        questionStr = _questionStr;
        betDeadlineBlock = _betDeadline;
        finalizeStartTime = _finalizeStartTime;
        queryStr = _queryStr;
    }

    function kill(address recipient) public onlyAdmin returns (bool ok) {
        selfdestruct(recipient);
        return true;
    }

    function resetFinalize() public onlyAdmin returns (bool ok) {
        finalized = false;
        return true;
    }

    function bet(uint yesOrNo) public payable returns (bool ok) {
        require(msg.value >= 0.01 ether);
        require(now <= betDeadlineBlock);

        Vote betVote;
        if (yesOrNo == 3) {
            winFunds = winFunds.safeAdd(msg.value);
            betVote = Vote.Win;
        } else if (yesOrNo == 1) {
            tieFunds = tieFunds.safeAdd(msg.value);
            betVote = Vote.Tie;
        } else if (yesOrNo == 0) {
            loseFunds = loseFunds.safeAdd(msg.value);
            betVote = Vote.Lose;
        }

        bets[msg.sender].bettor = msg.sender;
        bets[msg.sender].vote[uint(betVote)] = bets[msg.sender].vote[uint(betVote)].safeAdd(msg.value);
        bets[msg.sender].amount = bets[msg.sender].amount.safeAdd(msg.value);

        LogBet(msg.sender, betVote, msg.value);

        return true;
    }

    function withdraw() public returns (bool ok) {
        require(now > finalizeStartTime);
        require(finalized == true);

        Bet storage theBet = bets[msg.sender];
        require(theBet.amount > 0);
        require(theBet.withdrawn == false);

        theBet.withdrawn = true;

        uint totalFunds = winFunds.safeAdd(tieFunds).safeAdd(loseFunds);

        if (firstWithdraw) {
            if (totalFunds < 10 ether) {
                devCut = totalFunds.safeMul(5).safeDiv(100);
                devCutReceiver.transfer(devCut);
                firstWithdraw = false;
            }
            else if (totalFunds < 50 ether) {
                devCut = totalFunds.safeMul(4).safeDiv(100);
                devCutReceiver.transfer(devCut);
                firstWithdraw = false;
            }
            else {
                devCut = totalFunds.safeMul(3).safeDiv(100);
                devCutReceiver.transfer(devCut);
                firstWithdraw = false;
            }
        }

        totalFunds = totalFunds.safeSub(devCut);

        uint winningVoteFunds;
        uint betFunds;

        if (finalResult == 1 ) {
            require(theBet.vote[1] > 0);
            betFunds = theBet.vote[1];
            winningVoteFunds = winFunds;
        } else if (finalResult == 2) {
            require(theBet.vote[2] > 0);
            betFunds = theBet.vote[2];
            winningVoteFunds = loseFunds;
        } else if (finalResult == 3) {
            require(theBet.vote[3] > 0);
            betFunds = theBet.vote[3];
            winningVoteFunds = tieFunds;
        }
 
        uint withdrawAmount = totalFunds.safeMul(betFunds).safeDiv(winningVoteFunds);

        msg.sender.transfer(withdrawAmount);

        LogWithdraw(msg.sender, withdrawAmount);
        return true;
    }

    function __callback(bytes32 requestId, string result) {
        require(msg.sender == oraclize_cbAddress());
        finalResultStr = result;

        var s = finalResultStr.toSlice();
        var mid = s.beyond("[".toSlice()).until("]".toSlice());
        var delim = ", ".toSlice();
        var parts = new string[](mid.count(delim) + 1);
        for(uint i = 0; i < parts.length; i++) {
            parts[i] = mid.split(delim).toString();
        }
        localTeamScore = parseInt(parts[0]);
        visitorTeamScore = parseInt(parts[1]);

        if (localTeamScore > visitorTeamScore) {
            finalResult = 1;
        } else if (localTeamScore < visitorTeamScore) {
            finalResult = 2;
        } else if (localTeamScore == visitorTeamScore) {
            finalResult = 3;
        }

        if (finalResult != 0) {
            finalized = true;
        }
    }

    function finalize() public payable onlyAdmin {
        require(!finalized);
        require(now > finalizeStartTime);

        oraclize_query("URL", queryStr);
        
        LogFinalize(msg.sender);
    }

    function getMetadata()
        public 
        view
        returns (string _questionStr, uint _betDeadlineBlock, uint _finalizeStartTime, uint _winFunds, uint _loseFunds, uint _tieFunds, uint _finalResult, bool _finalized, uint _localTeamScore, uint _visitorTeamScore)
    {
        return (questionStr, betDeadlineBlock, finalizeStartTime, winFunds, loseFunds, tieFunds, finalResult, finalized, localTeamScore, visitorTeamScore);
    }

    function getTheBet(address account)
        public 
        view 
        returns (address _bettor, uint _winFunds, uint _loseFunds, uint _tieFunds, uint _amount, bool _withdrawn)
    {
        return (bets[account].bettor, bets[account].vote[1], bets[account].vote[2], bets[account].vote[3],bets[account].amount, bets[account].withdrawn);
    }

    modifier onlyAdmin {
        IPredictionMarket mkt = IPredictionMarket(owner);
        require(mkt.isAdmin(msg.sender));
        _;
    }
}

contract PredictionMarket is MultiOwnable
{
    using AddressSetLib for AddressSetLib.AddressSet;

    mapping(bytes32 => bool) public questionHasBeenAsked;
    AddressSetLib.AddressSet questions;

    event LogAddQuestion(address whoAdded, address questionAddress, string questionStr, uint betDeadlineBlock, uint finalizeStartTime);

    function PredictionMarket() {
        isAdmin[msg.sender] = true;
    }

    function kill(address recipient) public onlyAdmin returns (bool ok) {
        selfdestruct(recipient);
        return true;
    }

    function addQuestion(string questionStr, uint betDeadline, uint finalizeStartTime, string queryStr) 
        public 
        onlyAdmin 
        returns (bool ok, address questionAddr)
    {
        require(betDeadline > now);
        require(finalizeStartTime > betDeadline);

        bytes32 questionID = keccak256(questionStr);
        require(questionHasBeenAsked[questionID] == false);
        questionHasBeenAsked[questionID] = true;

        Question question = new Question(msg.sender, questionStr, betDeadline, finalizeStartTime, queryStr);
        questions.add(address(question));

        LogAddQuestion(msg.sender, address(question), questionStr, betDeadline, finalizeStartTime);

        return (true, address(question));
    }

    function removeQuestion(address addr) public onlyAdmin returns (bool ok) {
        questions.remove(addr);
        return (true);
    }

    function numQuestions() public view returns (uint) {
        return questions.size();
    }

    function getQuestionIndex(uint i) public view returns (address) {
        return questions.get(i);
    }

    function getAllQuestionAddresses() public view returns (address[]) {
        return questions.values;
    }
}
